mapper.select:
select gft.id as gft_id, gft.brcd as brcd, gft.expir_dt as expir_dt, gft.in_date as in_date, gft.img_path as img_path, gft.prod_code as prod_code,
            cate.name as cate_name, brd.name as brd_name, prod.name as prod_name, usr.email as requester



 order by gft.id
 limit #{amount}
 offset ${(pageNum - 1) * amount}

select count(*)
from gifticon gftT,
     (select * from cmmn_code where code_type = 'GFT_STUS' and code_name = '판매중' and enabled = '1') stusT
where prod_code = #{prdCode}
  and gftT.gft_stus_code = stusT.code

mapper.update:
update gifticon
set gft_stus_code = '003', up_date = now(), up_user = current_user()
where id = #{id}

update gifticon
set dc_prc = #{dcPrc}, dc_rate = #{dcRate}, up_date = now(), up_user = current_user()
where id = #{id}
