select id, email, name, tel_no, in_date, enabled, memo
from user
where id=#{userId};

select bcode.code_name, bank_account.acc
from bank_account,
     (select * from cmmn_code
      where code_type='BANK_NAME'
     ) as bcode
where user_id=#{userId}
  AND bank_account.bnk_code = bcode.code;

select balance from pnt_hist
where user_id=#{userId}
order by id desc
LIMIT 1;

select count(*) as deal_count, sum(pymt_prc) as paysum
from deal_detail
where user_id=#{userId}
group by user_id;

select count(*) as pqust_count
from cs_psnl_qust
where user_id=#{userId};
