mapper.select:

select wish.up_date, wish.prod_code, wish.user_id, brand.name as bname, prod.name as pname, prc, min(dc_prc)as dc_prc, max(dc_rate) as dc_rate, gifti.dc_rate,gft_stus_code, prod.img_path
from wish_list as wish
         INNER JOIN gifticon as gifti ON wish.prod_code = gifti.prod_code
         INNER JOIN product as prod ON gifti.prod_code = prod.code
         INNER JOIN brand ON prod.brd_code = brand.code
where wish.user_id= #{userId}
group by wish.prod_code
order by wish.up_date desc
LIMIT #{amount}
OFFSET ${(pageNum-1) * amount}
